let restaurants,neighborhoods,cuisines,lazyLoad;var map;var markers=[];document.addEventListener("DOMContentLoaded",event=>{fetchNeighborhoods();fetchCuisines();function logElementEvent(eventName,element){console.log((new Date).getTime(),eventName,element.getAttribute("data-src"))}lazyLoad=new LazyLoad({callback_load:function(element){logElementEvent("LOADED",element)},callback_set:function(element){logElementEvent("SET",element)}})});fetchNeighborhoods=(()=>{DBHelper.fetchNeighborhoods((error,neighborhoods)=>{if(error){console.error(error)}else{self.neighborhoods=neighborhoods;fillNeighborhoodsHTML()}})});fillNeighborhoodsHTML=((neighborhoods=self.neighborhoods)=>{const select=document.getElementById("neighborhoods-select");neighborhoods.forEach(neighborhood=>{const option=document.createElement("option");option.innerHTML=neighborhood;option.value=neighborhood;select.append(option)})});fetchCuisines=(()=>{DBHelper.fetchCuisines((error,cuisines)=>{if(error){console.error(error)}else{self.cuisines=cuisines;fillCuisinesHTML()}})});fillCuisinesHTML=((cuisines=self.cuisines)=>{const select=document.getElementById("cuisines-select");cuisines.forEach(cuisine=>{const option=document.createElement("option");option.innerHTML=cuisine;option.value=cuisine;select.append(option)})});window.initMap=(()=>{let loc={lat:40.722216,lng:-73.987501};self.map=new google.maps.Map(document.getElementById("map"),{zoom:12,center:loc,scrollwheel:false});updateRestaurants()});updateRestaurants=(()=>{const cSelect=document.getElementById("cuisines-select");const nSelect=document.getElementById("neighborhoods-select");const cIndex=cSelect.selectedIndex;const nIndex=nSelect.selectedIndex;const cuisine=cSelect[cIndex].value;const neighborhood=nSelect[nIndex].value;DBHelper.fetchRestaurantByCuisineAndNeighborhood(cuisine,neighborhood,(error,restaurants)=>{if(error){console.error(error)}else{resetRestaurants(restaurants);fillRestaurantsHTML()}})});resetRestaurants=(restaurants=>{self.restaurants=[];const ul=document.getElementById("restaurants-list");ul.innerHTML="";self.markers.forEach(m=>m.setMap(null));self.markers=[];self.restaurants=restaurants});fillRestaurantsHTML=((restaurants=self.restaurants)=>{const ul=document.getElementById("restaurants-list");restaurants.forEach(restaurant=>{ul.append(createRestaurantHTML(restaurant));lazyLoad.update()});addMarkersToMap()});createRestaurantHTML=(restaurant=>{const li=document.createElement("li");const imageContainer=document.createElement("div");imageContainer.className="restaurant-img-container";const imageSrcBig=DBHelper.imageUrlForRestaurant(restaurant);const imageSrcMedium=DBHelper.imageUrlForRestaurant(restaurant,"medium");const imageSrcSmall=DBHelper.imageUrlForRestaurant(restaurant,"small");const image=document.createElement("img");image.className="restaurant-img";image.setAttribute("data-src",imageSrcBig);image.setAttribute("data-srcset",`\n    ${imageSrcSmall} 150w,\n    ${imageSrcMedium} 300w,\n    ${imageSrcBig} 800w,\n  `);image.sizes=`\n    (max-width: 1200px) 50%,\n    (max-width: 767px) 100%,\n    270px\n  `;image.alt=`${restaurant.name} restaurant`;imageContainer.append(image);li.append(imageContainer);const name=document.createElement("h2");name.innerHTML=restaurant.name;li.append(name);const neighborhood=document.createElement("p");neighborhood.innerHTML=restaurant.neighborhood;li.append(neighborhood);const address=document.createElement("p");address.innerHTML=restaurant.address;li.append(address);const more=document.createElement("a");more.innerHTML="View Details";more.setAttribute("aria-label",restaurant.name+" View Details");more.href=DBHelper.urlForRestaurant(restaurant);li.append(more);return li});addMarkersToMap=((restaurants=self.restaurants)=>{restaurants.forEach(restaurant=>{const marker=DBHelper.mapMarkerForRestaurant(restaurant,self.map);google.maps.event.addListener(marker,"click",()=>{window.location.href=marker.url});self.markers.push(marker)})});